$(document).ready(function(){
	var modal = $('.reg'),
		overlay = $('.overlay'),
		link = $('.phone_form'),
		close = $('.close_btn');
		
			link.on('click', function(){
				overlay.show();
				modal.toggleClass('reg_active');

			});	
				close.click(function(){
				overlay.hide();
				modal.toggleClass('reg_active');
				
			});

	var modal_message = $('.reg_message'),
		overlay = $('.overlay'),
		link_message = $('.message_form'),
		close_btn_message = $('.close_btn_message');
		
			link_message.on('click', function(){
				overlay.show();
				modal_message.toggleClass('reg_message_active');

			});	
				close_btn_message.click(function(){
				overlay.hide();
				modal_message.toggleClass('reg_message_active');
				
			});
});